	/**
	 * @see https://www.unicode.org/reports/tr46/#ToASCII
	 *
	 * @param string $domainName
	 *
	 * @return string|false
	 */
	public static function toAscii($domainName)
	{
		$info = new Info();
		$labels = self::process((string) $domainName, $info);

		foreach ($labels as $i => $label) {
			// Only convert labels to punycode that contain non-ASCII code points
			if (1 === \preg_match('/[^\x00-\x7F]/', $label)) {
				try {
					$label = 'xn--'.self::punycodeEncode($label);
				} catch (\Exception $e) {
					$info->errors |= self::ERROR_PUNYCODE;
				}

				$labels[$i] = $label;
			}
		}

		self::validateDomainAndLabelLength($labels, $info);

		return $info->errors ? false : \implode('.', $labels);
	}

	/**
	 * @see https://www.unicode.org/reports/tr46/#ToUnicode
	 *
	 * @param string $domainName
	 *
	 * @return string|false
	 */
	public static function toUtf8($domainName)
	{
		$info = new Info();
		$labels = self::process((string) $domainName, $info);
		return 0 === $info->errors ? \implode('.', $labels) : false;
	}

	/**
	 * @param string $label
	 *
	 * @return bool
	 */
	private static function isValidContextJ(array $codePoints, $label)
	{
		if (!isset(self::$virama)) {
			self::$virama = static::getData('virama');
		}

		$offset = 0;

		foreach ($codePoints as $i => $codePoint) {
			if (0x200C !== $codePoint && 0x200D !== $codePoint) {
				continue;
			}

			if (!isset($codePoints[$i - 1])) {
				return false;
			}

			// If Canonical_Combining_Class(Before(cp)) .eq. Virama Then True;
			if (isset(self::$virama[$codePoints[$i - 1]])) {
				continue;
			}

			// If RegExpMatch((Joining_Type:{L,D})(Joining_Type:T)*\u200C(Joining_Type:T)*(Joining_Type:{R,D})) Then
			// True;
			// Generated RegExp = ([Joining_Type:{L,D}][Joining_Type:T]*\u200C[Joining_Type:T]*)[Joining_Type:{R,D}]
			if (0x200C === $codePoint && 1 === \preg_match(static::REGEX_ZWNJ, $label, $matches, \PREG_OFFSET_CAPTURE, $offset)) {
				$offset += \strlen($matches[1][0]);

				continue;
			}

			return false;
		}

		return true;
	}

	/**
	 * @see https://www.unicode.org/reports/tr46/#ProcessingStepMap
	 *
	 * @param string              $input
	 * @param array<string, bool> $options
	 *
	 * @return string
	 */
	private static function mapCodePoints($input, array $options, Info $info)
	{
		$str = '';
		$transitional = $options['Transitional_Processing'];

		foreach (self::utf8Decode($input) as $codePoint) {
			$data = self::lookupCodePointStatus($codePoint);

			switch ($data['status']) {
				case 'disallowed':
					$info->errors |= self::ERROR_DISALLOWED;

					// no break.

				case 'valid':
					$str .= \mb_chr($codePoint, 'utf-8');

					break;

				case 'ignored':
					// Do nothing.
					break;

				case 'mapped':
					$str .= $data['mapping'];

					break;

				case 'deviation':
					$info->transitionalDifferent = true;
					$str .= ($transitional ? $data['mapping'] : mb_chr($codePoint, 'utf-8'));

					break;
			}
		}

		return $str;
	}

	/**
	 * @see https://www.unicode.org/reports/tr46/#Processing
	 *
	 * @param string              $domain
	 *
	 * @return array<int, string>
